//import { Box, HStack, Input } from "@chakra-ui/react";

import React, { useEffect, useState } from "react";
import axios from "axios";

import "./App.css";

function App() {
  const [_id, setId] = useState("");
  const [informacao, setInformacao] = useState([]);
  const [questionsList, setQuestionsList] = useState([]);
  console.log(questionsList)

  useEffect(() => {
    if (_id) {
      obterInformacaoPorId(_id);
    }
  }, [_id]);

  async function obterInformacaoPorId(id) {
    try {
      const response = await axios.get(`http://localhost:8005/perguntas/${id}`);
      setInformacao(response.data);
    } catch (error) {
      // Handle errors
    }
  }

  const handleIdChange = (event) => {
    setId(event.target.value);
  };

  useEffect(() => {
    if (informacao.length > 0) {
      const extractedQuestions = informacao.find((info) => info._id === _id)
        ?.selectedQuestions
        ?.map((question) => (
          <li key={question.id}>{question.pergunta}</li> // Key prop added here!
        ));
      setQuestionsList(extractedQuestions);
    }
  }, [informacao, _id]);

  return (
    <div>
      <input
        type="text"
        value={_id}
        onChange={handleIdChange}
        placeholder="Insira o _id"
      />
    
      
    {questionsList.map((question) => (
  <li key={question.id}> 
    <p>{question.questionsList}</p>

  </li>
))}
    </div>
    
  );
}

export default App;















