import { Box, VStack, Input, Text, HStack, Button,Textarea } from "@chakra-ui/react";
import React, { useEffect, useState } from "react";
import axios from "axios";

function App() {
  const [posts, setPosts] = useState([]);
  const [selectedId, setSelectedId] = useState("");
  const [currentQuestion, setCurrentQuestion] = useState(null); // Track current question
  const [answer, setAnswer] = useState(""); // Store user's answer
  const [showAnswerSection, setShowAnswerSection] = useState(false); // Control answer section visibility

  useEffect(() => {
    if (selectedId) {
      getUserById(selectedId);
    }
  }, [selectedId]);

  const getUserById = async (id) => {
    try {
      const response = await axios.get(`http://localhost:8005/perguntas/${id}`);
      const questionList = response.data.map((post) =>
        post.selectedQuestions.map((question) => question)
      );
      setPosts(questionList);
      setCurrentQuestion(questionList[0]); // Start with the first question
      setShowAnswerSection(true); // Initially show answer section
    } catch (error) {
      // Handle errors
    }
  };

  const handleIdChange = (event) => {
    setSelectedId(event.target.value);
    setPosts([]); // Clear previous questions
    setCurrentQuestion(null);
    setAnswer("");
    setShowAnswerSection(false); // Hide answer section until questions fetched
  };

  const handleSubmitAnswer = async () => {
    try {
      // Replace with your actual answer submission logic
      // Here's an example using a mock API endpoint:
      await axios.post(`http://localhost:8005/perguntas/${currentQuestion._id}/answer`, {
        answer,
      });

      const remainingQuestions = posts.filter((q) => q !== currentQuestion);
      setPosts(remainingQuestions);
      setCurrentQuestion(remainingQuestions[0]); // Move to next question
      setAnswer("");

      // Optionally, display a success message or provide other feedback
    } catch (error) {
      // Handle errors
    }
  };

  const handleNextQuestion = () => {
    if (posts.length > 0) {
      const remainingQuestions = posts.filter((q) => q !== currentQuestion);
      setPosts(remainingQuestions);
      setCurrentQuestion(remainingQuestions[0]); // Move to next question
      setAnswer(""); // Clear answer input for the new question
    }
  };

  return (
    <div>
      <VStack spacing={8}>
        <Box w="100%" ml="5%" mt="5%">
          <Input
            className="wrap-input"
            id="cpfInput"
            value={selectedId}
            onChange={handleIdChange}
            placeholder="Enter _id to fetch data"
          />
        </Box>

        {currentQuestion && ( // Render only when a question is available
          <>
            <Box m="5">
              <Text as="h2">{`Pergunta ${posts.indexOf(currentQuestion) + 1}:`}</Text>
              <Text as="p">{currentQuestion.pergunta}</Text>
            </Box>

            {showAnswerSection && ( // Show answer section if necessary
              <VStack spacing={4} mt={4}>
                <Textarea
                  value={answer}
                  onChange={(e) => setAnswer(e.target.value)}
                  placeholder="Insira sua resposta aqui"
                />
                <HStack width="100%" justifyContent="space-between">
                  <Button onClick={handleSubmitAnswer}>Responder</Button>
                  {posts.length > 1 && ( // Only show "Próxima" button if more questions exist
                    <Button onClick={handleNextQuestion}>Próxima</Button>
                  )}
                </HStack>
              </VStack>
            )}
          </>
        )}
      </VStack>
      <Text as="p" mt={4}>Questão {posts.indexOf(currentQuestion) + 1} de {posts.length}</Text>
      {posts.length > 1 && (
  <Text as="p" mt={4}>Próxima pergunta: {posts[1].pergunta.slice(0, 50)}...</Text>
)}
<VStack spacing={4}>
  {posts.map((question, index) => (
    <Box key={question._id} border={index === posts.indexOf(currentQuestion) ? '2px solid blue' : '1px solid gray'} p={4}>
      <Text as="h4">{`Pergunta ${index + 1}:`}</Text>
      <Text as="p">{question.pergunta}</Text>
    </Box>
  ))}
</VStack>
    </div>
  );
}

export default App;
