    try {
      const response = await axios.get(`http://localhost:8005/perguntas/${id}`);
      const questionList = response.data.map(post => post.selectedQuestions.map(question => question.pergunta));
      setPosts(questionList); // Now posts holds an array of question arrays
    } catch (error) {
      // Handle errors
    }
  };
*/

const getUserById = async (id) => {
  try {
    const response = await axios.get(`http://localhost:8005/perguntas/${id}`);
    const questions = response.data.map((post) => post.selectedQuestions.map((question) => question));

    // Render questions directly instead of storing them
    questions.forEach((question, index) => {
      // Create elements for each question (e.g., Box, Text)
      // Add unique keys for React
      // Append elements to the DOM using libraries like ReactDOM
    });
  } catch (error) {
    // Handle errors
  }
};

  const handleIdChange = (event) => {
    setSelectedId(event.target.value);
    getUserById(event.target.value);
  };

  const ListRow = ({ index, data }) => {
    const question = data[index];

    return (
      <Box m={4}>
        <Text as="h4">{`Pergunta ${index + 1}:`}</Text>
        <Text as="p">{question}</Text>
      </Box>
    );
  };
  return (
    <div>
    <HStack spacing={8}>
          <Box w="100%" ml="5%" mt="5%">
            <Input
              className="wrap-input"
              id="cpfInput"
              value={selectedId}
              onChange={handleIdChange}
              placeholder="Enter _id to fetch data"
            />
          </Box>
          
        </HStack>
        {selectedId && (
    <WindowScroller>
      {({ height, onScroll }) => (
        <List
          height={height}
          itemCount={posts.length}
          itemSize={60} // Adjust item size based on your needs
          onItemsRendered={(info) => console.log('Rendered items:', info.visibleStartIndex, info.visibleStopIndex)}
          onScroll={onScroll}
        >
          {({ index, style }) => <ListRow index={index} data={posts} style={style} />}
        </List>
      )}
    </WindowScroller>
  )}

    </div>
  );
}
export default App;
