import { Box, HStack, Input } from "@chakra-ui/react";
import React, { useEffect, useState } from 'react';
import axios from 'axios';

// Assuming postData is fetched from an API endpoint
const fetchPostData = async () => {
  try {
    const response = await axios.get('your-api-endpoint');
    return response.data;
  } catch (error) {
    console.error(error);
    return {}; // Provide a default object to avoid errors
  }
};

function App() {
  const [posts, setPosts] = useState([]);
  const [selectedId, setSelectedId] = useState('');
  const [postData, setPostData] = useState({});

  useEffect(() => {
    fetchPostData().then(data => setPostData(data));
  }, []); // Fetch postData once on component mount

  useEffect(() => {
    if (selectedId) {
      getUserById(selectedId);
    }
  }, [selectedId]);

  const getUserById = async (id) => {
    // ... your implementation to fetch data using id
  };

  const handleIdChange = (event) => {
    setSelectedId(event.target.value);

    // Use postData if it's available, otherwise handle gracefully
    if (postData) {
      const postForId = postData[selectedId];
      if (postForId) {
        // Access and use the desired information from postForId
      } else {
        // Handle the case where no data found for the given id
      }
    } else {
      console.warn('postData not yet available');
    }
  };
  
  return (
    <div>
    <HStack spacing={8}>
          <Box w="100%" ml="5%" mt="5%">
            <Input
              className="wrap-input"
              id="cpfInput"
              value={selectedId}
              onChange={handleIdChange}
              placeholder="Enter _id to fetch data"
            />
          </Box>
          
        </HStack>
     <div >
   
        {posts.length > 0 && ( // Render questions only if available
        <div >
          <h2>Perguntas encontradas para o CPF :</h2>
          {posts.map((_id,questions,perguntas) => (
            <div key={_id}  >
             
            <p>{questions}</p>
              <p >{perguntas }</p>
            </div>
          ))}
           </div>
      )}

         </div>
         {posts.map((post) => (
  <div key={post._id} >
    <p>{post._id.propertyName1}</p>
    <p >{post._id.propertyName2 }</p>
  </div>
))}
    </div>
 
)
}

export default App;
















