import {HStack} from "@chakra-ui/react";
import React, { useEffect, useState} from "react";
import axios from "axios";


import "./App.css"


function App(){

  
  const url = "http://localhost:8005/questions";
  
  const [searchedCpf, setSearchedCpf] = useState('');
  const [posts, setPosts] = useState([]);

  const handleSearch = async () => {
    try {
      const response = await axios.get(`/questions/:${searchedCpf}`);
  
      setPosts(response.data); // Update state with fetched data
    } catch (error) {
      console.error(error);
      alert('Error fetching data! Please try again later.');
    }
  };


  function getUser() {
    axios.get(url)
      .then((response) => {
        setPosts(response.data);
      })
      .catch((error) => console.log(error));
  }


  useEffect(() => {
    getUser()
  }, []);


  

  return(
    <div className="App" >  
    <div  className="Colum"> 
      <div className="cont">
          <center><h2  >Selecionar Pergunta</h2></center>
            <HStack spacing="8">
              <input
                type="text"
                placeholder="Enter CPF"
                onChange={(e) => setSearchedCpf(e.target.value)}
              />
              <button onClick={handleSearch}>Search</button>
            <div>
                        
                    {posts.length > 0 ? (
                      <div>
                        {posts.map((post) => (
                          <div key={post._id}>
                            <p>{post.pergunta}</p>
                            <p>
                              {post.infoPaciente.map((info) => (
                                <p key={info.cpf}>
                                  {info.cpf}: {info.otherInfo}
                                </p>
                              ))}
                            </p>
                          </div>
                        ))}
                      </div>
                    ) : (
                      <p>No perguntas found for this CPF.</p>
                    )}

              </div> 
            </HStack>
            </div>
          
      </div>
    </div>
  )
}

export default App
