import { Box, VStack, Input } from "@chakra-ui/react";
import React, { useEffect, useState } from "react";
import axios from "axios";

import "./App.css";

function App() {
  const [posts, setPosts] = useState([]);
  const [selectedId, setSelectedId] = useState("");

  useEffect(() => {
    if (selectedId) {
      getUserById(selectedId);
    }
  }, [selectedId]);

  const getUserById = async (id) => {
    try {
      const response = await axios.get(`http://localhost:8005/questions/${id}`);
      const questionList = response.data.map((post) =>
        post.selectedQuestions.map((question) => question.pergunta)
      );
      setPosts(questionList);
    } catch (error) {
      // Handle errors
    }
  };

  const handleIdChange = (event) => {
    setSelectedId(event.target.value);
    getUserById(event.target.value);
  };

  return (
    <div>
      <VStack spacing={8}>
        <Box w="100%" ml="5%" mt="5%">
          <Input
            className="wrap-input"
            id="cpfInput"
            value={selectedId}
            onChange={handleIdChange}
            placeholder="Enter _id to fetch data"
          />
        </Box>

        {posts.length > 0 && (
          <>
            <h2>Perguntas encontradas para o CPF:</h2>
            <VStack spacing={4}> {/* Use VStack for vertical stacking */}
              {posts.map((question, index) => (
                <div className="perguntas" key={index}>
                  <h4>Pergunta {index + 1}:</h4>
                  <p>{question}</p>
                </div>
              ))}
            </VStack>
          </>
        )}
      </VStack>
    </div>
  );
}

export default App;
