import {FormLabel,Box,Input,HStack,FormControl,
  Center, Modal,Alert} from "@chakra-ui/react";
import InputMask from 'react-input-mask';
import React from 'react';
import axios from "axios";
import  {useState} from 'react'
import './cadasPac.css';


const CadastroPaciente = () => {

  const [name, setName] = useState('')
  const [cpfInput, setCpfInput] = useState('');
  const [dataN, setDataN] = useState('');
  const [naturalidade, setNaturalidade] = useState('');
  const [celular, setCelular] = useState('');
  const [telefone, setTelefone] = useState('');
  const [endereço,setEndereço] = useState('');
  const [cidade,setCidade] = useState('');
  const [sexo, setSexo] = useState('Masculino'); 
  const [modalIsOpen,setIsOpen] = useState(false);
  const [isCpfRegistered, setIsCpfRegistered] = useState(false); // New state for CPF check

  
  useEffect(() => {
    if (cpfInput.length === 14) { // Check after full CPF input
      checkCpfRegistration(cpfInput);
    }
  }, [cpfInput]);

  const checkCpfRegistration = async (cpf) => {
    try {
      const response = await axios.get(`http://localhost:8003/cadastro?cpf=${cpf}`);
      setIsCpfRegistered(response.data.length > 0);
    } catch (error) {
      console.error(error);
      alert("Erro ao verificar CPF.");
    }
  };


  function handleOpenModal(){setIsOpen(true)}
  function handleCloseModal(){ setIsOpen(false) }
  function handleCpfChange(event) {setCpfInput(event.target.value); }

  function isValidCPF(cpf) {
    const regex = /^\d{3}\.\d{3}\.\d{3}-\d{2}$/;
    return regex.test(cpf);
  }


  const btnFunction = async () => {
    if (isValidCPF(cpfInput)) {
      if (!isCpfRegistered) {
        const cpf = cpfInput;

        try {
          const response = await axios.post("http://localhost:8003/Cadastro", {
            _id: cpf,
            cpf,
            name,
            dataN,
            naturalidade,
            celular,
            telefone,
            endereço,
            cidade,
            sexo,
          });
          console.log(response);
          alert("Cadastro realizado com sucesso!");
        } catch (error) {
          console.error(error);
          alert("Erro ao salvar Cadastro. Tente novamente mais tarde.");
        }
      } else {
        alert("Cpf já cadastrado!");
      }
    } else {
      alert("CPF inválido. Verifique e tente novamente.");
    }
  };


  return (
    <div className='CadastroPaciente'>
        <button 
          type="submit" 
          className="login-form-btn" 
          onClick={handleOpenModal}>
          Cadastrar
        </button>

    <Modal 
      isOpen={modalIsOpen}
      onRequestClose={handleCloseModal}
      zindex={1999}
      position="absolute"
    >
    <div className="modal-content" zindex={1999}>
      <div className="container-modal">
        <div className="wrap-modal">
          <form>
              <Center
                as="header"
                h={50}
                w={430}
                bg="#f29ef3"
                color="white"
                fontWeight="bold"
                fontSize="4xl"
                pb="8"
              >
              <h2>Cadastro de Paciente</h2>
              </Center><br></br>
            <FormControl display="flex" flexDir="column" gap="4">
                <HStack spacing="4">
                  <Box w="100%">
                    <FormLabel htmlFor="nome">Nome Completo</FormLabel>
                    <Input id="nome" type="nome" 
                    onChange={e => setName(e.target.value)}
                    />
                  </Box>
                  <Box w="100%">
                    <FormLabel htmlFor="cpf">Cpf</FormLabel>
                    {isCpfRegistered && (
              <Alert status="error" mt={2}>
                Este CPF já está cadastrado. Por favor, digite um CPF diferente.
              </Alert>
            )}
                    <InputMask
                       mask="999.999.999-99"
                       className={cpfInput !== '' ? 'has-val input' : 'input'}
                       type="CPF"
                       onChange={handleCpfChange}
                    /> 
                  </Box>
                </HStack>
                <HStack spacing="4">
                  <Box w="100%">
                    <FormLabel htmlFor="nasc">Data de Nascimento</FormLabel>
                    <Input id="nasc" type="date"
                    onChange={e => setDataN(e.target.value)}
                    />
                  </Box>
                  <Box w="100%">
                    <FormLabel htmlFor="natural">Naturalidade</FormLabel>
                    <Input id="natural" 
                    onChange={e => setNaturalidade(e.target.value)}
                    />
                  </Box>
                </HStack>
                <HStack spacing="4">
                  <Box w="100%">
                    <FormLabel htmlFor="cel">Celular</FormLabel>
                    <Input id="cel" type="number"
                    onChange={e => setCelular(e.target.value)}
                    />
                  </Box>
                  <Box w="100%">
                    <FormLabel htmlFor="Tefone">Telefone</FormLabel>
                    <Input id="Tefone" type="number"
                    onChange={e => setTelefone(e.target.value)}
                    />
                  </Box>
                </HStack>
                <HStack spacing="4">
                  <Box w="100%">
                    <FormLabel htmlFor="endereco">Endereço</FormLabel>
                    <Input id="endereco" 
                    onChange={e => setEndereço(e.target.value)}
                    />
                  </Box>
                  <Box w="100%">
                    <FormLabel htmlFor="cidade">Cidade</FormLabel>
                    <Input id="cidade"
                    onChange={e => setCidade(e.target.value)} 
                    />
                  </Box>
                </HStack>
            </FormControl>
              <div>
                <p>Selecione seu Sexo</p>
                <select value={sexo}  onChange={e => setSexo(e.target.value)} >
                  <option value="Feminino">Feminino</option>
                  <option value="Masculino">Masculino</option>
                </select>
              </div>
            <br></br><br></br>
                <div className="container-login-form-btn">
                  <button type="submit" 
                  className="login-form-btn" 
                  onClick={btnFunction}>Cadastrar Paciente</button>
                  <button type="submit" 
                  className="login-form-btn"  
                  onClick={handleCloseModal}>close</button>
                </div>
          </form>
        </div>
      </div>
    </div> 
   
    </Modal>
  </div>
  );
};

export default CadastroPaciente;
