import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Box, HStack, Input, Text } from '@chakra-ui/react';

function App() {
  const [selectedCpf, setSelectedCpf] = useState('');
  const [posts, setPosts] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchQuestions = async () => {
      if (selectedCpf.length === 14) { // Validate CPF length
        try {
          const response = await axios.get(`http://localhost:8005/questions/${selectedCpf}`);
          setPosts(response.data);
          
          setError(null);
        } catch (err) {
          setError('Houve um erro ao buscar as perguntas.'); // User-friendly error message
        }
      }
    };

    fetchQuestions();
  }, [selectedCpf]);

  const handleCpfChange = (event) => {
    setSelectedCpf(event.target.value);
  };

  return (
    <div>
      <HStack spacing={8}>
        <Box w="100%" ml="5%" mt="5%">
          <Input
            type="text"
            className="wrap-input"
            id="cpfInput"
            placeholder="Enter CPF"
            value={selectedCpf}
            onChange={handleCpfChange}
            maxLength={14} // Enforce CPF length restriction
          />
          {error && <Text color="red.500">{error}</Text>}
        </Box>
      </HStack>

      <div className="perguntas">
        {posts.length === 0 && !error &&
          <Text>Nenhum CPF inserido.</Text>
        }
        {error && <Text color="red.500">{error}</Text>}
        {posts.map((questionArray, index) => (
          <div key={index}>
            <h2>Pergunta {index + 1}:</h2>
            <p>{questionArray[0]}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
