import { Box, HStack, Input } from "@chakra-ui/react";
import React, { useEffect, useState } from "react";
import axios from "axios";

import "./App.css";

function App() {
  const [posts, setPosts] = useState([]);
  const [selectedId, setSelectedId] = useState("");
  const url = `http://localhost:8005/perguntas/${id}`;

  function getUser() {
    axios.get(url)
      .then((response) => {
        setPosts(response.data);
      })
      .catch((error) => console.log(error));
  }


  useEffect(() => {
    getUser()
  }, []);




  useEffect(() => {
   
    if (selectedId) {
      getUserById(selectedId);
    }
  }, [selectedId]);

  const getUserById = async (id) => {
    try {
      const response = await axios.get(`http://localhost:8005/perguntas/${id}`);
      const questionList = response.data.map(post => post.selectedQuestions.map(question => question.pergunta));
      setPosts(questionList); // Now posts holds an array of question arrays
    } catch (error) {
      // Handle errors
    }
  };


  const handleIdChange = (event) => {
    setSelectedId(event.target.value);
    getUserById(event.target.value);
  };


  
  return (
    <div>
    <HStack spacing={8}>
          <Box w="100%" ml="5%" mt="5%">
            <Input
              className="wrap-input"
              id="cpfInput"
              value={selectedId}
              onChange={handleIdChange}
              placeholder="Enter _id to fetch data"
            />
          </Box>
          
        </HStack>
     <div >
   
        {posts.length > 0 && ( // Render questions only if available
        <div >
          <h2>Perguntas encontradas para o CPF :</h2>
          {posts.map((_id,questions,perguntas) => (
            <div key={_id}  >
             
            <p>{questions}</p>
              <p >{perguntas }</p>
            </div>
          ))}
           </div>
      )}

         </div>
         {posts.length === 0 ? (
              <p>Carregando...</p>
            ) : (
              posts.map((post) => (
                <div className="perguntas" key={post._id}>
                <p>{post.perguntas}</p>
             
              </div>
              ))
              )}
    </div>
 
)
}

export default App;
