import { Box, HStack, Input } from "@chakra-ui/react";
import React, { useEffect, useState } from "react";
import axios from "axios";

import "./App.css";

function App() {
  const [posts, setPosts] = useState([]);
  const [selectedId, setSelectedId] = useState("");
  console.log(posts)

  useEffect(() => {
    // Fetch data initially if a selectedId is already present
    if (selectedId) {
      getUserById(selectedId);
    }
  }, [selectedId]);

  const getUserById = async (id) => {
    try {
      const response = await axios.get(`http://localhost:8005/questions/${id}`);
      setPosts(response.data);
    } catch (error) {
      console.error("Error fetching data by ID:", error);
      // Handle errors gracefully, e.g., display an error message
    }
  };

  const handleIdChange = (event) => {
    setSelectedId(event.target.value);
    getUserById(event.target.value);
  };

  return (
    // ... other JSX elements ...
    <HStack spacing={8}>
      <Box w="100%" ml="5%" mt="5%">
        <Input
          className="wrap-input"
          id="cpfInput"
          value={selectedId}
          onChange={handleIdChange}
          placeholder="Enter _id to fetch data"
        />
      </Box>
    </HStack>
    // ... other JSX elements ...
  );
}

export default App;
