  }, [selectedId]);

  const getUserById = async (id) => {
    try {
      const response = await axios.get(`http://localhost:8005/perguntas/${id}`);
      const questionList = response.data.map(post => 
        post.selectedQuestions.map(question => question.pergunta));
      setPosts(questionList); // Now posts holds an array of question arrays
    } catch (error) {
      // Handle errors
    }
  };


  const handleIdChange = (event) => {
    setSelectedId(event.target.value);

    getUserById(event.target.value);
  };*/


  
  return (
    <div>
    
         <div>
      <input
        type="text"
        value={_id}
        onChange={handleIdChange}
        placeholder="Insira o _id"
      />
      {informacao.map((info) => (
        <div key={info._id}>
          <p>{info.propriedadeDesejada}</p>
        </div>
      ))}
    </div>
    </div>
 
)
}

export default App;
















