import { Box, HStack, Input, List, ListItem, Button } from "@chakra-ui/react";
import React, { useEffect, useState } from "react";
import axios from "axios";
import { useRef } from "react";

function App() {
  const [pergunta, setPergunta] = useState([]);
  const [selectedId, setSelectedId] = useState("");
  const [answers, setAnswers] = useState([]); // State to store answers
  const isSubmitDisabled = answers.some((a) => !a.trim()); // Disable button if any answer is empty
  const answersRefs = useRef([]);

  useEffect(() => {
    answersRefs.current = Array(pergunta.length).fill(null); // Initialize refs
  }, [pergunta]);

  useEffect(() => {
    if (selectedId) {
      fetchQuestions(selectedId);
    }
  }, [selectedId]);

  const fetchQuestions = async (id) => {
    try {
      const response = await axios.get(`http://localhost:8005/perguntas/${id}`);
      const questionList = response.data.map((post) =>
        post.selectedQuestions.map((question) => question.pergunta)
      );
      setPergunta(questionList.flat());
      setAnswers(Array(questionList.length).fill("")); // Initialize answers with empty strings
    } catch (error) {
      // Handle errors here
    }
  };

  const handleIdChange = (event) => {
    setSelectedId(event.target.value);
  };

  const handleAnswerChange = (index, answer) => {
    setAnswers((prevAnswers) => [
      ...prevAnswers.slice(0, index),
      answer,
      ...prevAnswers.slice(index + 1),
    ]);
  };

  async function handleSubmit() {
    const _id = selectedId; // Assuming _id is used for identification

    const dataToSend = pergunta.map((post, index) => ({
      post: post, // Use corrected field name 'pergunta'
      answer: answers[index],
    }));

    try {
      const response = await axios.post("http://localhost:8005/response", {
        selectedId: _id,
        responses: dataToSend,
      });

      console.log("Respostas salvas com sucesso!", response.data);
      // Handle success, e.g., clear form, show confirmation message
    } catch (error) {
      console.error(error);
      // Handle errors, e.g., display error messages
    }
  }

  return (
    <div>
      <HStack spacing={8}>
        <Box w="100%" ml="5%" mt="5%">
          <Input
            className="wrap-input"
            id="cpfInput"
            value={selectedId}
            onChange={handleIdChange}
            placeholder="Enter _id to fetch data"
          />
        </Box>
      </HStack>

      {pergunta.length > 0 ? (
        <List spacing={3}>
          {pergunta.map((question, index) => (
            <ListItem key={index}>
              {question}
              <br />
              <Input
                type={answersRefs.current[index]} // Attach ref for uncontrolled input
                value={answers[index]}
                onChange={(e) => handleAnswerChange(index, e.target.value)}
              />
            </ListItem>
          ))}
          <Button isDisabled={isSubmitDisabled} onClick={handleSubmit}>
            Enviar Todas as Respostas
          </Button>
        </List>
      ) : (
        <Box textAlign="center">Loading...</Box>
      )}
    </div>
  );
}

export default App;







