import { Box, HStack, Input, List, ListItem, Button } from "@chakra-ui/react";
import React, { useEffect, useState } from "react";
import axios from "axios";

function App() {
  const [posts, setPosts] = useState([]);
  const [selectedId, setSelectedId] = useState("");
  const [answers, setAnswers] = useState([]); // State to store answers
  const [isSubmitDisabled, setIsSubmitDisabled] = useState(true);

  useEffect(() => {
    if (selectedId) {
      fetchQuestions(selectedId);
    }
  }, [selectedId]);

  const fetchQuestions = async (id) => {
    try {
      const response = await axios.get(`http://localhost:8005/perguntas/${id}`);
      const questionList = response.data.map(post => post.selectedQuestions.map(question => question.pergunta));
      setPosts(questionList.flat());
      setAnswers(Array(questionList.length).fill("")); // Initialize answers with empty strings
      setIsSubmitDisabled(true); // Disable button until all answers are provided
    } catch (error) {
      // Handle errors here
    }
  };

  const handleIdChange = (event) => {
    setSelectedId(event.target.value);
  };

  const handleAnswerChange = (index, answer) => {
    setAnswers((prevAnswers) => {
      return [...prevAnswers.slice(0, index), answer, ...prevAnswers.slice(index + 1)];
    });
    setIsSubmitDisabled(answers.some((a) => !a.trim())); // Disable button if any answer is empty
  };

  const handleSubmit = async () => {
    // Send the answers to the server
    try {
      const response = await axios.post(`http://localhost:8005/submit-answers`, { answers });
      console.log("Response from server:", response);
      // Handle success response (e.g., display a confirmation message)
    } catch (error) {
      // Handle errors here
    }
  };

  return (
    <div>
      <HStack spacing={8}>
        <Box w="100%" ml="5%" mt="5%">
          <Input
            className="wrap-input"
            id="cpfInput"
            value={selectedId}
            onChange={handleIdChange}
            placeholder="Enter _id to fetch data"
          />
        </Box>
      </HStack>

      {posts.length > 0 ? (
        <List spacing={3}>
          {posts.map((question, index) => (
            <ListItem key={index}>
              {question}<br></br>
              <Input
                value={answers[index]}
                onChange={(e) => handleAnswerChange(index, e.target.value)}
                placeholder="Enter answer"
              />
            </ListItem>
          ))}
          <Button disabled={isSubmitDisabled} onClick={handleSubmit}>
            Submit Answers
          </Button>
        </List>
      ) : (
        <Box textAlign="center">Loading...</Box>
      )}
    </div>
  );
}

export default App;






