import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Box, HStack, Input, Text } from '@chakra-ui/react';

function App() {
  const [selectedCpf, setSelectedCpf] = useState('');
  const [posts, setPosts] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchQuestions = async () => {
      if (selectedCpf.length === 14) { // Validate CPF length
        setError(null); // Clear previous errors

        try {
          const response = await axios.get(`http://localhost:8005/questions/${selectedCpf}`);
          setPosts(response.data); // Set all questions for the retrieved CPF
        } catch (err) {
          if (err.response?.status === 404) {
            setError('CPF não encontrado.');
          } else {
            setError('Houve um erro ao buscar as perguntas.');
          }
        }
      }
    };

    fetchQuestions();
  }, [selectedCpf]);

  const handleCpfChange = (event) => {
    setSelectedCpf(event.target.value.replace(/\D/g, '')); // Remove non-digits
  };

  return (
    <div>
      <HStack spacing={8}>
        <Box w="100%" ml="5%" mt="5%">
          <Input
            type="text"
            className="wrap-input"
            id="cpfInput"
            placeholder="Enter CPF"
            value={selectedCpf}
            onChange={handleCpfChange}
            maxLength={14} // Enforce CPF length restriction
          />
          {error && <Text color="red.500">{error}</Text>}
        </Box>
      </HStack>

      {posts.length > 0 && ( // Render questions only if available
        <div className="perguntas">
          <h2>Perguntas encontradas para o CPF {selectedCpf}:</h2>
          {posts.map((questionArray, index) => (
            <div key={index}>
              <h4>Pergunta {index + 1}:</h4>
              <p>{questionArray[0]}</p>
            </div>
          ))}
        </div>
      )}

      {/* Consider adding a section to handle cases where no questions are found */}
    </div>
  );
}

export default App;
