import {Box,HStack,Alert} from "@chakra-ui/react";
import React, { useEffect, useState} from "react";
import axios from "axios";


import "./App.css"


function App(){

  
  const url = "http://localhost:8003/PerguntaSelecionadaPacientes";
  
  const [posts, setPosts] = useState('');
  console.log(posts)

  
  const [infoPaciente, setInfoPaciente] = useState();
  console.log(infoPaciente)
  const [showError, setShowError] = useState(false);

  function searchByCpf(cpf) {
    setShowError(false);
  
    axios
      .get(`http://localhost:8003/cadastro?cpf=${cpf}`)
      .then((response) => {
        const data = response.data;
        if (data.length > 0) {
         
          const matchingResult = data.find((item) => item.cpf === cpf);
  
          if (matchingResult) {
            setInfoPaciente(matchingResult); 
           
          } else {
            setShowError(true); 
          }
        } else {
          setShowError(true); 
        }
      })
      .catch((error) => {
        console.error(error);
        setShowError(true);
      });
  }
  






  function getUser() {
    axios.get(url)
      .then((response) => {
        setPosts(response.data);
      })
      .catch((error) => console.log(error));
  }


  useEffect(() => {
    getUser()
  }, []);


  

  return(
    <div className="App" >  
    <div  className="Colum"> 
      <div className="cont">
          <center><h2  >Selecionar Pergunta</h2></center>
            <HStack spacing="8">
              <Box w="100%" ml="5%" mt="5%">
                <h4>Buscar Paciente</h4>      
                <p>
                  {showError && (
                    <Alert className="alert" status="error"> *Usuário não encontrado no banco de dados.</Alert>
                  )}
                </p>
             
              </Box>
           
            </HStack>
            </div>
          
      </div>
    </div>
  )
}

export default App
