import { Box, VStack, Input, Text } from "@chakra-ui/react";
import React, { useEffect, useState } from "react";
import axios from "axios";

function App() {
  const [posts, setPosts] = useState([]);
  const [selectedId, setSelectedId] = useState("");
  console.log(selectedId)

  useEffect(() => {
    if (selectedId) {
      getUserById(selectedId);
    }
  }, [selectedId]);

  const getUserById = async (id) => {
    try {
      const response = await axios.get(`http://localhost:8005/perguntas/${id}`);
      const questionList = response.data.map((post) =>
        post.selectedQuestions.map((question) => question.pergunta)
      );
      setPosts(questionList);
    } catch (error) {
      // Handle errors
    }
  };

  const handleIdChange = (event) => {
    setSelectedId(event.target.value);
    setPosts([]); // Clear previous questions when searching new ID
  };

  return (
    <div>
      <VStack spacing={8}>
        <Box w="100%" ml="5%" mt="5%">
          <Input
            className="wrap-input"
            id="cpfInput"
            value={selectedId}
            onChange={handleIdChange}
            placeholder="Enter _id to fetch data"
          />
        </Box>

        {posts.length > 0 && (
          <>
            <h2>Perguntas encontradas para o CPF:</h2>
            <VStack spacing={4}>
              {posts.map((question, index) => (
                <Box key={index} borderWidth={1} borderRadius={4} p={4}>
                
                  <Text as="p">{question}
                  <Input type="text" placeholder="Digite sua resposta aqui" />
                  </Text>
                  <br />
                </Box>
              ))}
            </VStack>
          </>
        )}
      </VStack>
    </div>
  );
}

export default App;
