import { Box, HStack, Input, List, ListItem } from "@chakra-ui/react";
import React, { useEffect, useState } from "react";
import axios from "axios";

function App() {
  const [posts, setPosts] = useState([]);
  const [selectedId, setSelectedId] = useState("");
  //const [answers, setAnswers] = useState([]);
  // console.log(answers)
  
  useEffect(() => {
    if (selectedId) {
      fetchQuestions(selectedId);
    }
  }, [selectedId]);

  const fetchQuestions = async (id) => {
    try {
      const response = await axios.get(`http://localhost:8005/perguntas/${id}`);
      const questionList = response.data.map(post => post.selectedQuestions.map(question => question.pergunta));
      setPosts(questionList.flat()); // Flatten nested arrays if necessary
    } catch (error) {
      // Handle errors here (e.g., display an error message)
      console.error("Error fetching questions:", error);
    }
  };

  const handleIdChange = (event) => {
    setSelectedId(event.target.value);
  };

  /*const handleAnswerChange = (index, answer) => {
    setAnswers((prevAnswers) => [...prevAnswers, { question: posts[index], answer }]);
  };*/

  async function handleSubmit() {
    const answers = posts.map((question, index) => ({
      question,
      answer: document.querySelector(`input[placeholder="Digite sua resposta..."][data-question-index="${index}"]`).value,
    }));
  
    try {
      await axios.post('http://localhost:8005/response', { resposta: answers });
      console.log('Respostas salvas com sucesso!');
    } catch (error) {
      console.error(error);
    }
  }


  return (
    <div>
      <HStack spacing={8}>
        <Box w="100%" ml="5%" mt="5%">
          <Input
            className="wrap-input"
            id="cpfInput"
            value={selectedId}
            onChange={handleIdChange}
            placeholder="Enter _id to fetch data"
          />
        </Box>
      </HStack>

      {posts.length > 0 ? (
        <List spacing={3}>
          {posts.map((question, index) => (
            <ListItem key={index}>{question}<br></br>
            <input
              className="space"
              type="text"
              placeholder="Digite sua resposta..."
              value={answer.answer || ''}
              data-question-index={index}
            />
            </ListItem>
          ))}
          <button onClick={handleSubmit} >Enviar</button>
        </List>
      ) : (
      
        <Box textAlign="center">Loading...</Box>
      )}
    </div>
  );
}

export default App;





