import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Box, HStack, Input, Alert } from '@chakra-ui/react';
import CadastroPaciente from './CadastroP/cadasPaciente';
// ... other imports

function App() {
  const [posts, setPosts] = useState([]);
  const [selectedQuestions, setSelectedQuestions] = useState([]);
  const [cpfInput, setCpfInput] = useState('');
  const [infoPaciente, setInfoPaciente] = useState(null);
  const [showError, setShowError] = useState(false);

  useEffect(() => {
    // Fetch available questions on app load
    axios.get('http://localhost:8003/questions')
      .then((response) => setPosts(response.data))
      .catch((error) => console.error(error));
  }, []);

  const searchByCpf = async (cpf) => {
    setShowError(false);

    try {
      const response = await axios.get(`http://localhost:8003/cadastro?cpf=${cpf}`);

      if (response.data.length > 0) {
        const matchingResult = response.data.find((item) => item.cpf === cpf);

        if (matchingResult) {
          // Fetch existing selected questions for the CPF
          const selectedData = await axios.get(`http://localhost:8003/selecao/${cpfInput}`);
          const patientQuestions = selectedData.data.selectedQuestions;
          setSelectedQuestions(patientQuestions.map((q) => q.perguntaId));

          setInfoPaciente(matchingResult);
        } else {
          setShowError(true);
        }
      } else {
        setShowError(true);
      }
    } catch (error) {
      console.error(error);
      setShowError(true);
    }
  };

  const handlePergunta = (e) => {
    const isChecked = e.target.checked;
    const questionId = e.target.value;

    setSelectedQuestions(
      isChecked
        ? [...selectedQuestions, questionId]
        : selectedQuestions.filter((id) => id !== questionId)
    );
  };

  const sendDataToBackend = async () => {
    try {
      const dataToSend = selectedQuestions.map((questionId) => ({
        perguntaId: questionId,
        pergunta: posts.find((post) => post._id === questionId).perguntas,
      }));

      await axios.post('http://localhost:8003/selecao', {
        selectedQuestions: dataToSend,
        infoPaciente: infoPaciente,
        _id: cpfInput,
      });

      console.log('Data sent to backend:', dataToSend);
      alert('Perguntas enviadas com sucesso!');
      setSelectedQuestions([]);
      setCpfInput('');
      setInfoPaciente(null);
    } catch (error) {
      console.error('Error sending questions:', error);
      alert('Erro ao enviar perguntas');
    }
  };

  return(
    <div className="App">
    <div className="Colum">
      <div className="cont">
        <center>
          <h2>Selecionar Pergunta</h2>
        </center>
        <HStack spacing="8">
          <Box w="100%" ml="5%" mt="5%">
            <h4>Buscar Paciente</h4>
            <p>
              {showError && (
                <Alert className="alert" status="error">
                  Paciente n√£o encontrado no banco de dados.
                </Alert>
              )}
            </p>
            <Input
              className="wrap-input"
              id="cpfInput"
              value={cpfInput}
              onChange={(e) => setCpfInput(e.target.value)}
            />
            {infoPaciente && (
              <div key={infoPaciente._id}>
                <p>CPF: {infoPaciente.cpf}</p>
                <p>Nome: {infoPaciente.name}</p>
              </div>
            )}
          </Box>
          <Box w="35%" mr="5%" mt="5%">
            <center>
              <h4>
                Cadastrar Paciente
              </h4>
            </center>
                  <CadastroPaciente/>
              </Box>
            </HStack>
      </div><br></br><br></br>
          <div className="perguntas">
            {posts.length === 0 ? (
              <p>Carregando...</p>
            ) : (
              posts.map((post) => (
                <div className="perguntas" key={post._id}>
                <p>{post.perguntas}</p>
                <input 
                  type="checkbox"
                  id={post._id} 
                  value={post._id}
                  checked={selectedQuestions.includes(post._id)}
                  onChange={handlePergunta}
                />
              </div>
              ))
            )}
          </div>
          <div className="container-Finalizar-form-btn">
            <button className="Finalizar-form-btn" 
            onClick={sendDataToBackend} >Finalizar</button>
          </div>
          </div>
    </div>
  )
}

export default App
