import { Box, HStack, Input, List, ListItem, Button, Alert } from "@chakra-ui/react";
import React, { useEffect, useState } from "react";
import axios from "axios";
import { useRef } from "react";

function App() {
  const [posts, setPosts] = useState([]);
  const [_id, setSelectedId] = useState("");

  const [answers, setAnswers] = useState([]); 
  const [infoPaciente, setInfoPaciente] = useState({});
  console.log(infoPaciente)// State to store answers
  const isSubmitDisabled = answers.some((a) => !a.trim()); // Disable button if any answer is empty
  const answersRefs = useRef([]);

  useEffect(() => {
    answersRefs.current = Array(posts.length).fill(null); // Initialize refs
  }, [posts]);

  useEffect(() => {
    if (_id) {
      fetchQuestions(_id);
    }
  }, [_id]);

const fetchQuestions = async (id) => {
  try {
    const response = await axios.get(`http://localhost:8005/perguntas/${id}`);
    const questionList = response.data.map((post) =>
      post.selectedQuestions.map((question) => question.pergunta)
    );
    setPosts(questionList.flat());
    setAnswers(Array(questionList.length).fill("")); //Inicialize respostas com strings vazias

    // Acessar e armazenar o infoPaciente a partir da resposta
    const { infoPaciente } = response.data[0]; //  infoPaciente estao no primeiro objeto
    setInfoPaciente(infoPaciente);

  } catch (error) {
    // Lide com erros aqui
  }
};
  const handleIdChange = (event) => {
    setSelectedId(event.target.value);
  };

  const handleAnswerChange = (index, answer) => {
    setAnswers((prevAnswers) => [
      ...prevAnswers.slice(0, index),
      answer,
      ...prevAnswers.slice(index + 1),
    ]);
  };

  async function handleSubmit() {
    const selectedId = _id ; // Assuming _id is used for identification

    const dataToSend = posts.map((pergunta, index) => ({
      pergunta: pergunta, // Use corrected field name 'pergunta'
      resposta: answers[index],
    }));

    try {
      const response = await axios.post("http://localhost:8005/response", {
        infoPaciente,
        _id: selectedId,
        responses: dataToSend,
      });
      Alert("Obrigada")
      console.log("Respostas salvas com sucesso!", response.data);
      // Handle success, e.g., clear form, show confirmation message
    } catch (error) {
      console.error(error);
      // Handle errors, e.g., display error messages
    }
  }

  return (
    <div>
      <HStack spacing={8}>
        <Box w="100%" ml="5%" mt="5%">
          <Input
            className="wrap-input"
            id="cpfInput"
            value={_id}
            onChange={handleIdChange}
            placeholder="Enter _id to fetch data"
          />
        </Box>
      </HStack>

      {posts.length > 0 ? (
        <List spacing={3}>
          {posts.map((question, index) => (
            <ListItem key={index}>
              {question}
              <br />
              <Input
                type={answersRefs.current[index]} // Attach ref for uncontrolled input
                value={answers[index]}
                onChange={(e) => handleAnswerChange(index, e.target.value)}
              />
            </ListItem>
          ))}
          <Button isDisabled={isSubmitDisabled} onClick={handleSubmit}>
            Enviar Todas as Respostas
          </Button>
        </List>
      ) : (
        <Box textAlign="center">Loading...</Box>
      )}
    
    </div>
  );
}

export default App;
