import { Box, HStack, Input } from "@chakra-ui/react";
import React, { useEffect, useState } from "react";
import axios from "axios";

import "./App.css";

function App() {
  const [selectedCpf, setSelectedCpf] = useState("");
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    if (selectedCpf) {
      getUserByCpf(selectedCpf);
    }
  }, [selectedCpf]);

  const getUserByCpf = async (cpf) => {
    try {
      const response = await axios.get(`http://localhost:8005/questions/${cpf}`); // Adjust endpoint
      const allQuestions = response.data.map(post => post.selectedQuestions.map(question => question.pergunta));

      // Filter questions directly, avoiding unnecessary nested maps
      const filteredQuestions = allQuestions.filter(question => question.includes(cpf));
      setPosts(filteredQuestions);
    } catch (error) {
      // Handle errors
    }
  };

  const handleCpfChange = (event) => {
    setSelectedCpf(event.target.value);
  };

  return (
    <div>
      <HStack spacing={8}>
        <Box w="100%" ml="5%" mt="5%">
          <Input
            className="wrap-input"
            id="cpfInput"
            value={selectedCpf}
            onChange={handleCpfChange}
            placeholder="Enter CPF to fetch questions"
          />
        </Box>
      </HStack>

      <div className="perguntas">
        {posts.map((question, index) => (
          <div key={index}>
            <h2>Pergunta {index + 1}:</h2>
            <p>{question}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
