//import { Box, HStack, Input } from "@chakra-ui/react";

import React, { useEffect, useState } from "react";
import axios from "axios";

import "./App.css";

function App() {
  const [_id, setId] = useState("");
  const [informacao, setInformacao] = useState([]);
  const [questionsList, setQuestionsList] = useState([]); // State variable to store questions

  useEffect(() => {
    if (_id) {
      obterInformacaoPorId(_id);
    }
  }, [_id]);

  async function obterInformacaoPorId(id) {
    try {
      const response = await axios.get(`http://localhost:8005/perguntas/${id}`);
      setInformacao(response.data);
    } catch (error) {
      // Handle errors
    }
  }

  const handleIdChange = (event) => {
    setId(event.target.value);
  };

  useEffect(() => {
    if (informacao.length > 0) { // Use length > 0 for clarity
      // Extract questions only when informacao is available
      const extractedQuestions = informacao.find((info) => info._id === _id)
        ?.selectedQuestions // Check for existence before access
        ?.map((question) => (
          <li key={question.id}>{question.text}</li>
        ));
      setQuestionsList(extractedQuestions);
    }
  }, [informacao, _id]); // Update questionsList when informacao or _id changes

  return (
    <div>
      <input
        type="text"
        value={_id}
        onChange={handleIdChange}
        placeholder="Insira o _id"
      />
      {questionsList.length > 0 ? (
        <ul>{questionsList}</ul> // Render questions within an unordered list
      ) : (
        <p>Nenhuma pergunta encontrada.</p> // Show message if no questions
      )}
    </div>
  );
}

export default App;

















