import React, { useEffect, useState } from "react";
import axios from "axios";

import "./App.css";

function App() {
  const [posts, setPosts] = useState([]);
  const [selectedId, setSelectedId] = useState("");
  console.log(setPosts)

  useEffect(() => {
   
    if (selectedId) {
      getUserById(selectedId);
    }
  }, [selectedId]);

